mapper.select:
SELECT
FROM db_leave_message lw WHERE lw.receiverId = #{id} AND lw.level = #{level} and lw.type = '0'
and (lw.title like '%${title}%' or lw.content like '%${title}%')
and lw.isRead = #{isRead}
ORDER BY lw.isRead ASC, lw.time DESC LIMIT ${offset},${size}

SELECT count(*) FROM db_leave_message lw WHERE lw.receiverId = #{id} AND lw.level = #{level} and lw.type = '0'
and (lw.title like '%${title}%' or lw.content like '%${title}%')
and lw.isRead = #{isRead}
