mapper.select:
SELECT d.*,u.name as createUserName FROM db_discussion d left join sys_user u on d.createUserId=u.id where 1=1 and d.level = #{level}
and d.title like CONCAT('%', #{title },'%')
ORDER BY d.status asc,d.createTime desc limit ${offset},${size}

select npcId 
from db_choice c
where 1=1 and c.disId = #{disId}
