mapper.select:
select e.*, u.name as createName from v_excesuggestion e left join sys_user u on u.id = e.createUserId where 1=1 and e.level = #{level}
and e.title like CONCAT('%', #{title },'%')
order by e.createTime desc LIMIT ${offset},${size}

select e.*, u.name as createName from v_exceworkreport e left join sys_user u on u.id = e.createUserId where 1=1 and e.level = #{level}
and e.title like CONCAT('%', #{title },'%')
order by e.createTime desc LIMIT ${offset},${size}
