mapper.update:
UPDATE sug_suggestion SET
	code = #{code}
WHERE id = #{id}

	UPDATE sug_suggestion SET
		attevaluation = #{attevaluation},
		resevaluation = #{resevaluation},
		status = #{status}
WHERE id = #{id}

mapper.select:
SELECT a.id, a.status, a.unitID, a.operatorID, a.operatorDate, a.createDate, a.remark , o.name as unitName, u.name as operatorName, u2.name as 'sug.firstWriter', sug.id as 'sug.id', sug.year as 'sug.year', sug.code as 'sug.code', sug.title as 'sug.title', sug.secondary as 'sug.secondary', sug.sequence as 'sug.sequence', sug.classify as 'sug.classify', sug.status as 'sug.status' FROM sug_assignList a LEFT JOIN sug_suggestion sug on a.suggestionID = sug.id LEFT JOIN sys_office o on o.id = a.unitID LEFT JOIN sys_user u on u.id = a.operatorID LEFT JOIN sys_user u2 on u2.id = sug.firstWriterID WHERE a.unitID = ${unitID} and sug.type=${type_value} and sug.level= ${level_code}
and a.status=1 and a.backFlag = 0
and ( a.status=2 or a.status = -1 )
and a.backFlag = 1
ORDER BY sug.code ASC limit ${offset},${size}

SELECT
sug.* ,
o.name as mission,
u.name as firstWriter
FROM
sug_suggestion sug
LEFT JOIN sys_office o on o.id = sug.teamID
LEFT JOIN
sys_user u on u.id =
sug.firstWriterID
WHERE 1=1

	and sug.firstWriterID = #{firstWriterID }


	and (sug.title like CONCAT('%', #{title },'%')
	or keyWord
	like CONCAT('%',#{title},'%') )


	and sug.secondary = #{secondary }


	and sug.sequence = #{sequence }


	and sug.type = #{type }


	and sug.classify = #{classify }


	and sug.excellentFlag = #{excellentFlag }


	and sug.importantFlag = #{importantFlag }


	and sug.status = #{status }

ORDER BY sug.id DESC

SELECT count(*)
FROM sug_suggestion sug
WHERE sug.level=${level_code}
AND sug.teamID = ${team_id}
